import React, { Component } from "react";
const axios = require("axios");

class Upload extends Component {
  apiEndPoint = "http://localhost:5000/api/videos";

  handleSubmit = e => {
    e.preventDefault();

    let formData = new FormData();
    let inputVideos = document.querySelector("#inputVideos");

    for (var i = 0; i < inputVideos.files.length; i++) {
      let file = inputVideos.files[i];
      formData.append("video", file);
    }

    // Send a POST request
    axios
      .post(this.apiEndPoint, formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      })
      .then(function(response) {
        // handle success
        console.log(response);

        // Reset the form
        document.getElementById("uploadForm").reset();
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  };

  inputChangeHandler = e => {
    this.setState({
      inputVideos: e
    });
  };

  render() {
    return (
      <div>
        <form
          id="uploadForm"
          encType="multipart/form-data"
          action={this.apiEndPoint}
          method="post"
          onSubmit={this.handleSubmit}
        >
          <div className="form-group">
            <label htmlFor="inputUploadVideos">
              Choose video file/s to upload:
            </label>
            <input
              type="file"
              name="inputVideos"
              id="inputVideos"
              className="form-control-file form-control-md"
              onChange={e => this.inputChangeHandler(e.target.files)}
              accept="video/*"
              multiple
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default Upload;
