import React, { Component } from "react";
import axios from "axios";
import { apiHost } from "../utils/restAPI";
import VideoList from "./videoList";

class Home extends Component {
  state = {
    videos: []
  };

  componentDidMount = () => {
    let data = {};

    // Fetch all videos
    axios
      .get(apiHost + "/api/videos")
      .then(response => {
        data = response.data;

        this.setState({
          videos: data
        });
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  };

