import React, { Component } from "react";
const axios = require("axios");

class Upload extends Component {
  state = { inputVideos: {} };
  apiEndPoint = "http://localhost:5000/api/videos";

  handleSubmit = e => {
    e.preventDefault();

    // Send a POST request
    axios({
      method: "post",
      url: this.apiEndPoint,
      data: { videos: this.state.inputVideos }
    })
      .then(function(response) {
        // handle success
        console.log(response);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  };

  inputChangeHandler = e => {
    this.setState({
      inputVideos: e.FileList
    });
  };

  render() {
    return (
      <div>
        {/* <form>
          <input type="file" name="userPhoto" multiple />
          <input type="submit" value="Upload" name="submit" />
          <input type="text" id="random" name="random" />
          <br />
          <span id="status"></span>
        </form> */}

        <form
          id="uploadForm"
          encType="multipart/form-data"
          action={this.apiEndPoint}
          method="post"
          onSubmit={this.handleSubmit}
        >
          <div className="form-group">
            <label htmlFor="inputUploadVideos">
              Choose video file/s to upload:
            </label>
            <input
              type="file"
              name="inputVideos"
              id="inputUploadVideos"
              className="form-control-file"
              onChange={e => this.inputChangeHandler(e.target.files)}
              multiple
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default Upload;
