import React, { Component } from "react";
const axios = require("axios");

class Upload extends Component {
  state = { formFields: {} };
  apiEndPoint = "http://localhost:5000/api/videos";

  constructor() {
    super();

    this.state = {
      formFields: { inputVideos: [] }
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    // Send a POST request
    axios({
      method: "post",
      url: this.apiEndPoint,
      data: this.state.formFields
    })
      .then(function(response) {
        // handle success
        console.log(response);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  };

  inputChangeHandler = e => {
    let formFields = { ...this.state.formFields };
    formFields["inputVideos"] = e.target.value;
    this.setState({
      formFields
    });
  };

  render() {
    return (
      <div>
        {/* <form>
          <input type="file" name="userPhoto" multiple />
          <input type="submit" value="Upload" name="submit" />
          <input type="text" id="random" name="random" />
          <br />
          <span id="status"></span>
        </form> */}

        <form
          id="uploadForm"
          encType="multipart/form-data"
          action={this.apiEndPoint}
          method="post"
          onSubmit={this.handleSubmit}
        >
          <div className="form-group">
            <label htmlFor="inputUploadVideos">
              Choose video file/s to upload:
            </label>
            <input
              type="file"
              name="inputVideos"
              id="inputUploadVideos"
              className="form-control-file"
              value={this.state.formFields.inputVideos}
              onChange={this.inputChangeHandler()}
              multiple
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default Upload;
