import React, { Component } from "react";
import { apiHost } from "../utils/restAPI";
const axios = require("axios");

class Upload extends Component {
  state = {
    showFormMessage: false,
    formSuccess: false,
    formMessage: ""
  };

  handleSubmit = e => {
    e.preventDefault();

    let formData = new FormData();
    let inputVideos = document.querySelector("#inputVideos");

    for (var i = 0; i < inputVideos.files.length; i++) {
      let file = inputVideos.files[i];
      formData.append("video", file);
    }

    // Send a POST request
    axios
      .post(apiHost + "/api/videos", formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      })
      .then(response => {
        // handle success
        const { data } = response;
        //console.log(data);

        this.setState({
          formSuccess: true,
          showFormMessage: true,
          formMessage: (
            <React.Fragment>
              Upload success! <br />
              {data.map(file => {})}
            </React.Fragment>
          )
        });

        // Reset the form
        document.getElementById("uploadForm").reset();
      })
      .catch(error => {
        // handle error
        console.log(error);
      });
  };

  inputChangeHandler = e => {
    this.setState({
      inputVideos: e
    });
  };

  displayFormMessage = () => {
    const formMessageClass = this.state.formSuccess
      ? "alert alert-success"
      : "alert alert-danger";

    if (this.state.showFormMessage) {
      return (
        <div className={formMessageClass} role="alert">
          {this.state.formMessage}
        </div>
      );
    }
  };

  render() {
    return (
      <div className="form-box">
        {this.displayFormMessage()}
        <form
          id="uploadForm"
          encType="multipart/form-data"
          action={this.apiEndPoint}
          method="post"
          onSubmit={this.handleSubmit}
        >
          <div className="form-group">
            <label htmlFor="inputUploadVideos">
              Choose video file/s to upload:
            </label>
            <input
              type="file"
              name="inputVideos"
              id="inputVideos"
              className="form-control-file form-control-md"
              onChange={e => this.inputChangeHandler(e.target.files)}
              accept="video/*"
              multiple
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}

export default Upload;
